    // Approach 2

    node *swap_nodes(node *head, int i, int j)
    {
        // base case
        if (head == NULL || head->next == NULL || i == j || i < 0 || j < 0)
            return head;

        // case 1: i = 0 and contiguous
        // case 2: i = 0 and non-contiguous
        // case 3: i = not zero and contiguous
        // case 4: i = not zero and non contiguos

        if (i > j)
        {
            int temp = i;
            i = j;
            j = temp;
        }

        // i is < j now.
        if (i == 0)
        {
            // contiguos
            if (j == 1)
            {
                node *p = head->next;
                head->next = p->next;
                p->next = head;
                return p;
            }
            else // zero and non contiguos
            {
                node *jprev = head;
                for (int a = 0; a < j - 1 && jprev != NULL; a++)
                    jprev = jprev->next;

                if (jprev == NULL || jprev->next == NULL) //overrun
                    return head;

                node *jth = jprev->next;
                node *headn = head->next;

                jprev->next = head;
                head->next = jth->next;
                jth->next = headn;
                return jth; // it's the head now
            }
        }
        else
        {
            if (j == i + 1) // contiguous
            {
                // go to the ith node
                node *iprev = head;
                for (int a = 0; a < i - 1 && iprev != NULL; a++)
                    iprev = iprev->next;

                if (iprev == NULL || iprev->next == NULL || iprev->next->next == NULL) // overrun check
                    return head;

                node *ith = iprev->next;
                node *jth = ith->next;
                node *jnext = jth->next;

                ith->next = jnext;
                jth->next = ith;
                iprev->next = jth;
                return head;
            }
            else
            {
                node *iprev = head;
                int a = 0;

                for (a = 0; a < i - 1 && iprev != NULL; a++)
                    iprev = iprev->next;

                if (iprev == NULL || iprev->next == NULL) //overrun check
                    return head;

                node *jprev = iprev;

                for (; a < j - 1 && jprev != NULL; a++)
                    jprev = jprev->next;

                if (jprev == NULL || jprev->next == NULL) // oevrrun check
                    return head;

                node *ith = iprev->next;
                node *jth = jprev->next;
                node *inext = ith->next;
                node *jnext = jth->next;

                iprev->next = jth;
                jprev->next = ith;
                ith->next = jnext;
                jth->next = inext;

                return head;
            }
        }
    }

