    class Deque
    {
        // Complete this class
    public:
        int *arr;
        int size, capacity;
        int firstIndex, nextIndex;
        Deque(int cap = 0)
        {
            arr = new int[cap];
            size = 0;
            capacity = cap;
            firstIndex = cap - 1; // front of the queue
            nextIndex = 0;        // ready to be written at
        }

        void insertRear(int data)
        {
            if (size == capacity)
            {
                cout << -1 << endl;
                return;
            }
            if (size == 0)
            {
                firstIndex = capacity - 1;
                arr[0] = data;
                nextIndex = 1;
            }
            else
            {
                arr[nextIndex] = data;
                nextIndex = (nextIndex + 1) % capacity;
            }
            size++;
        }

        void insertFront(int data)
        {
            if (size == capacity)
            {
                cout << -1 << endl;
                return;
            }
            if (size == 0)
            {
                arr[capacity - 1] = data;
                firstIndex = capacity - 2;
                nextIndex = 0;
            }
            else
            {
                arr[firstIndex] = data;

                if (firstIndex == 0)
                    firstIndex = capacity - 1;
                else
                    firstIndex--;
            }
            size++;
        }

        void deleteFront()
        {
            if (size == 0)
            {
                cout << -1 << endl;
                return;
            }
            firstIndex = (firstIndex + 1) % capacity;
            size--;
        }

        void deleteRear()
        {
            if (size == 0)
            {
                cout << -1 << endl;
                return;
            }
            if (nextIndex == 0)
                nextIndex = capacity - 1;
            else
                nextIndex--;
            size--;
        }

        int getFront()
        {
            if (size == 0)
                return -1;
            // int front = (firstIndex+1)%capacity;
            return arr[(firstIndex + 1) % capacity];
        }

        int getRear()
        {
            if (size == 0)
                return -1;
            if (nextIndex == 0)
                return arr[capacity - 1];
            else
                return arr[nextIndex - 1];
        }

        ~Deque()
        {
            delete[] arr;
        }
    };
