    void zigZagOrder(BinaryTreeNode<int> *root)
    {
        if (root == NULL)
            return;

        queue<BinaryTreeNode<int> *> q;
        q.push(root);
        q.push(NULL);
        // O(n) space complexity

        list<BinaryTreeNode<int> *> prit;

        BinaryTreeNode<int> *temp;

        bool flag = false;

        while (!q.empty())
        {
