                    prit.pop_front();
                }
                cout << "\n";
                if (q.empty())
                    return;
                q.push(NULL);
                flag = !flag;
                continue;
            }
            if (flag == false)
                prit.push_back(temp);
            else
                prit.push_front(temp);
            if (temp->left != NULL)
                q.push(temp->left);
            if (temp->right != NULL)
                q.push(temp->right);
        }
    }

    // Using two stacks and L->R and R->L child check
    void zigZagOrder(BinaryTreeNode<int> *root)
    {
        if (root == NULL)
            return;
        stack<BinaryTreeNode<int> *> sf;     // forward
        stack<BinaryTreeNode<int> *> sb;     // backwards
        stack<BinaryTreeNode<int> *> *p, *c; // parents, children

        stack<BinaryTreeNode<int> *> *switcher;
        sf.push(root);
        p = &sf;
        c = &sb;

        bool flag = false; // this makes us shift the array from left to right and right to left
        while (!p->empty())
        {
            if (flag == false)
            {
                while (!p->empty())
                {
                    // print the conent of the parent
                    cout << p->top()->data << " ";
                    if (p->top()->left != NULL)
                        c->push(p->top()->left);

                    if (p->top()->right != NULL)
                        c->push(p->top()->right);
                    p->pop();
                }
                // done with the parent
                cout << "\n";
                flag = true;
            }
            else
            {
                while (!p->empty())
                {
                    // print the conent of the parent
                    cout << p->top()->data << " ";
                    if (p->top()->right != NULL)
                        c->push(p->top()->right);
                    if (p->top()->left != NULL)
                        c->push(p->top()->left);
                    p->pop();
                }
                // done with the parent
                cout << "\n";
                flag = false;
            }

            // switch parent and child.
            switcher = p;
            p = c;
            c = switcher;
        }
    }

